SELECT *, (select code.code_name from code where code.code='product_delivery_type' and code.code_value =product.product_delivery_type) as product_delivery_type_name, (select Z.product_made_company_name from product_made_company Z where Z.product_made_company_cd=product.product_made_company_cd) as product_made_company_name, (select code.code_name from code where code.code='product_sale_yn' and code.code_value =product.product_sale_yn) as product_sale_yn_name, (select product_file.file_link from product_file where product_file.key_id = product.product_cd and product_file.file_order = 1) as file_1, (select product_file.file_link from product_file where product_file.key_id = product.product_cd and product_file.file_order = 2) as file_2, (select product_file.file_link from product_file where product_file.key_id = product.product_cd and product_file.file_order = 3) as file_3, (select product_file.file_link from product_file where product_file.key_id = product.product_cd and product_file.file_order = 4) as file_4, (select product_file.file_link from product_file where product_file.key_id = product.product_cd and product_file.file_order = 5) as file_5, (select product_file.file_link from product_file where product_file.key_id = product.product_cd and product_file.file_order = 6) as file_6 FROM product where 1=1 and product_live_type is not null
${item} LIKE CONCAT('%', #{searchKeyword},'%' )
AND product_name LIKE CONCAT('%', #{product_name},'%' ) AND product_sale_yn= #{product_sale_yn} AND product_use_yn= #{product_use_yn} AND find_in_set(#{product_ct},replace(product_ct,'|',',')) > 0 AND find_in_set('Y',replace(${mainViewType},'|',',')) > 0 order by ${orderByKey} ${orderByValue} order by product_id DESC LIMIT ${rowStart-1}, ${displayRowCount}


SELECT
    A.*,
    (select Z.product_cd from product Z where Z.product_id=A.product_id) as product_cd,
    (select Z.product_name from product Z where Z.product_id=A.product_id) as product_name
FROM product_payment_history A  where 1=1


 AND A.coupon_id= #{pd_payment_user_id} 
AND A.reg_date >= #{start_date} 
AND A.reg_date < #{end_date} 
 AND product_cd= #{product_cd} 
 AND product_cd= #{product_cd} 
 AND product_name= #{product_name} 
 AND A.coupon_paid_class_cd= #{pd_payment_cd} 
 AND A.coupon_paid_class_cd= #{pd_payment_class_cd}


SELECT
    count(*) as cnt
FROM product_payment_history A  where 1=1


 AND A.coupon_id= #{pd_payment_user_id} 
AND A.reg_date >= #{start_date} 
AND A.reg_date < #{end_date} 
 AND product_cd= #{product_cd} 
 AND product_cd= #{product_cd} 
 AND product_name= #{product_name} 
 AND A.coupon_paid_class_cd= #{pd_payment_cd} 
 AND A.coupon_paid_class_cd= #{pd_payment_class_cd}
